function xoa_dau(n){return n=n.replace(/à|á|ạ|ả|ã|â|ầ|ấ|ậ|ẩ|ẫ|ă|ằ|ắ|ặ|ẳ|ẵ/g,"a"),n=n.replace(/è|é|ẹ|ẻ|ẽ|ê|ề|ế|ệ|ể|ễ/g,"e"),n=n.replace(/ì|í|ị|ỉ|ĩ/g,"i"),n=n.replace(/ò|ó|ọ|ỏ|õ|ô|ồ|ố|ộ|ổ|ỗ|ơ|ờ|ớ|ợ|ở|ỡ/g,"o"),n=n.replace(/ù|ú|ụ|ủ|ũ|ư|ừ|ứ|ự|ử|ữ/g,"u"),n=n.replace(/ỳ|ý|ỵ|ỷ|ỹ/g,"y"),n=n.replace(/đ/g,"d"),n=n.replace(/À|Á|Ạ|Ả|Ã|Â|Ầ|Ấ|Ậ|Ẩ|Ẫ|Ă|Ằ|Ắ|Ặ|Ẳ|Ẵ/g,"A"),n=n.replace(/È|É|Ẹ|Ẻ|Ẽ|Ê|Ề|Ế|Ệ|Ể|Ễ/g,"E"),n=n.replace(/Ì|Í|Ị|Ỉ|Ĩ/g,"I"),n=n.replace(/Ò|Ó|Ọ|Ỏ|Õ|Ô|Ồ|Ố|Ộ|Ổ|Ỗ|Ơ|Ờ|Ớ|Ợ|Ở|Ỡ/g,"O"),n=n.replace(/Ù|Ú|Ụ|Ủ|Ũ|Ư|Ừ|Ứ|Ự|Ử|Ữ/g,"U"),n=n.replace(/Ỳ|Ý|Ỵ|Ỷ|Ỹ/g,"Y"),n.replace(/Đ/g,"D")}function formatHTML(n){return $("<p>"+n+"<\/p>").text()}function decodeHtml(n){var t=document.createElement("textarea");return t.innerHTML=n,t.value}function extract_numberic(n){try{return n=n.match(/\d/g),n.join("")}catch(t){return""}}function encrypt_phone(n){let t="";if(n!=undefined&&n.length>0){let i=new Date,r=("0"+i.getDate()).slice(-2),u=("0"+(i.getMonth()+1)).slice(-2),f=i.getFullYear(),e=f.toString()+u.toString()+r.toString()+n;t=btoa(e)}return t}function decrypt_phone(n){let t="";if(n!=undefined&&n.length>0){let i=atob(n);t=i.substr(8)}return t}function isFunction(n){return typeof n=="function"?!0:!1}
