function Dropdown_Set_Position(n){try{let t=n.offset(),i=$(window).scrollTop(),r=n.outerWidth();n.attr("data-Offset",t.top);n.css({top:t.top-i,width:r,position:"fixed",transition:"none"});$(window).scroll(function(){let t=parseInt(n.css("top"));if(t!=0){let t=$(this).scrollTop(),i=Number(n.attr("data-Offset"));n.css("top",i-t)}})}catch(t){}}function Dropdown_Remove_Position(n){try{$(window).unbind("scroll");n.removeAttr("style data-Offset")}catch(t){}}